package java_project;

public class for_loop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
	//looping means to continue based on a given condition	
	//debugging means to execute a program based on debugging points.controls the exeucation of the program
		for (int i=0; //starting value i=0
			i<30;
				i+=1) // condition
		{
		System.out.println(i);	
			
			 
	
		}
				
				
		
		
	}

	

	}
