package java_project;

public class if_else {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int x=10;
		int t=12;
		int j=15;
		
		if (x>t){
			System.out.println("x is more then t");
		}

		else {
			System.out.println("t is more then x");
			
		}
	if(j>t)	
	{
		System.out.println("j is more than t");
	}
	
	
	}

}
